pub enum Event {
    Closed,
    Resized {
        width: u32,
        height: u32,
    },
    LostFocus,
    GainedFocus,
    TextEntered {
        unicode: char,
    },
    KeyPressed {
        code: Key,
        alt: bool,
        ctrl: bool,
        shift: bool,
        system: bool,
    },
    KeyReleased {
        code: Key,
        alt: bool,
        ctrl: bool,
        shift: bool,
        system: bool,
    },
    MouseWheelScrolled {
        wheel: Wheel,
        delta: f32,
        x: i32,
        y: i32,
    },
    MouseButtonPressed {
        button: Button,
        x: i32,
        y: i32,
    },
    MouseButtonReleased {
        button: Button,
        x: i32,
        y: i32,
    },
    MouseMoved {
        x: i32,
        y: i32,
    },
    MouseEntered,
    MouseLeft,
    JoystickButtonPressed {
        joystickid: u32,
        button: u32,
    },
    JoystickButtonReleased {
        joystickid: u32,
        button: u32,
    },
    JoystickMoved {
        joystickid: u32,
        axis: Axis,
        position: f32,
    },
    JoystickConnected {
        joystickid: u32,
    },
    JoystickDisconnected {
        joystickid: u32,
    },
    TouchBegan {
        finger: u32,
        x: i32,
        y: i32,
    },
    TouchMoved {
        finger: u32,
        x: i32,
        y: i32,
    },
    TouchEnded {
        finger: u32,
        x: i32,
        y: i32,
    },
    SensorChanged {
        type_: crate::window::sensor::Type,
        x: f32,
        y: f32,
        z: f32,
    },
}
